rule "volatility connscan normalize"
when
    to_string($message.plugin) == "connscan"
then

    let source = split(":", to_string($message.LocalAddress));
    set_field("src_ip", source[0]);
    set_field("src_port", source[1]);
    remove_field("LocalAddress");

    let dest = split(":", to_string($message.RemoteAddress));
    set_field("dst_ip", dest[0]);
    set_field("dst_port", dest[1]);
    remove_field("RemoteAddress");

    rename_field("PID", "pid");

    let concat_0 = concat("connscan plugin found process with pid ", to_string($message.pid));
    let concat_1 = concat(concat_0, " with connection from ");
    let concat_2 = concat(concat_1, to_string($message.src_ip));
    let concat_3 = concat(concat_2, " to ");
    let concat_4 = concat(concat_3, to_string($message.dst_ip));

    set_field("message", concat_4);
end

rule "volatility pslist normalize"
when
    to_string($message.plugin) == "pslist"
then
    rename_field("Hnds", "handle_count");
    rename_field("Name", "process_name");
    rename_field("PID", "pid");
    rename_field("PPID", "ppid");
    rename_field("Start", "process_start_time");
    rename_field("Thds", "thread_count");
    rename_field("TypicalServices", "typical_service_name");
    rename_field("PotentiallyMaliciousProcess", "potentially_malicious");

    set_field("process_name",lowercase(to_string($message.process_name)));

    set_field("message", concat("pslist plugin found process with name ", to_string($message.process_name)));
end

